$(document).ready(function () {
  let isMobile = window.matchMedia(
    "only screen and (max-width: 576px)"
  ).matches;

  if (isMobile) {
    listMobileElement = document.getElementsByClassName("is_mobile");
    for (let i = 0; i < listMobileElement.length; i++) {
      const element = listMobileElement[i];
      element.style.display = "block";
    }

    listMobileElement = document.getElementsByClassName("is_pc");
    for (let i = 0; i < listMobileElement.length; i++) {
      const element = listMobileElement[i];
      element.style.display = "none";
    }

    listMobileElement = document.getElementsByClassName("col-none");
    for (let i = 0; i < listMobileElement.length; i++) {
      const element = listMobileElement[i];
      element.style.display = "none";
    }
  } else {
    listMobileElement = document.getElementsByClassName("is_mobile");
    for (let i = 0; i < listMobileElement.length; i++) {
      const element = listMobileElement[i];
      element.style.display = "none";
    }

    listMobileElement = document.getElementsByClassName("is_pc");
    for (let i = 0; i < listMobileElement.length; i++) {
      const element = listMobileElement[i];
      element.style.display = "block";
    }
  }
//   AOS.init();
});

